import React, {useEffect, useState} from "react";
import UserCharacterCard from "./UserCharacterCard"

function Account( {user} ) {
  const [achievements, setAchievements] = useState([])
  useEffect(() => {
    fetch(`/users_achievements_by_user/${user.id}`)
    .then(r => r.json())
    .then(achievements => {
      const achievement_fks = achievements.map(userAchievement => userAchievement.achievement_fk)
      console.log(achievement_fks)
        achievement_fks.forEach(achievement_fk => {
          fetch(`/achievements/${achievement_fk}`)
          .then(r => r.json())
          .then(achievement => {
            console.log(achievement)
            setAchievements([...achievements, achievement])
          })
        })
      //   
    })
  }, [user.id])
  console.log("look at this one", achievements)



  // console.log(usersAchievements)




  return (
    <div>
      <h1>Account Details</h1>
      <p>Username: {user.username}</p>
      <h3>Your Achievements: </h3>
    </div>
  )
}

export default Account
