import React, {useEffect, useState} from "react";
import UserCharacterCard from "./UserCharacterCard"

function Account( {user} ) {
  const [usersAchievements, setUsersAchievements] = useState()
  useEffect(() => {
    fetch(`/users_achievements_by_user/${user.id}`)
    .then(r => r.json())
    .then(achievements => setUsersAchievements(achievements))
  }, [user.id])

  console.log(usersAchievements)

  const achievement_fks = usersAchievements.map(userAchievement => userAchievement.achievement_fk)
  console.log(achievement_fks)
  useEffect(() => {
    achievement_fks.forEach(achievement_fk => {
      fetch(`/achievements/${achievement_fk}`)
      .then(r => r.json())
      .then(achievement => console.log(achievement))
    })
  }, [achievement_fks])


  return (
    <div>
      <h1>Account Details</h1>
      <p>Username: {user.username}</p>
      <h3>Your Achievements: </h3>
    </div>
  )
}

export default Account
