import React from "react";
import { NavLink } from 'react-router-dom';

function Home( { user } ) {

  if (user) return (
    <>
      <div className="title-div">
        <h1 className="title">Welcome to The Forest</h1>
        <div className="start-button-container">
          {user? <NavLink className="start-button" to="/character_select">START GAME</NavLink> : <></> }
        </div>
      </div>
      <div className="account">
        <h2>Your Playthroughs</h2>
        {userCharacterCards}
        {showNewCharacterForm === false ? <button onClick={() => setShowNewCharacterForm(true)}>New Playthrough</button> :
        <div className="new-character-form-div">
          <form className="new-character-form" onSubmit={handleNewCharacter}>
            <h2>Choose Your New Character</h2>
            <label>Name:
              <input 
                type="text"
                name="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </label>
            <br/>
            <Button variant="fill" color="primary" type="submit">
              {isLoading ? "Loading..." : "Create"}
            </Button>
            <br/>
            {errors.map((err) => (
              <Error key={err}>{err}</Error>
            ))}
          </form>
        </div>}
      </div>
    </>
  )

  return (
    <>
      <div className="title-div">
        <h1 className="title">Welcome to The Forest</h1>
      </div>
    </>
  )
}

export default Home
