import React, {useEffect, useState} from "react";
import UserCharacterCard from "./UserCharacterCard"

function CharacterSelect( {user, setSelectedCharacter} ) {
  const [showNewCharacterForm, setShowNewCharacterForm] = useState(false)
  const [name, setName] = useState('')
  const [errors, setErrors] = useState([]);
  const [isLoading, setIsLoading] = useState(false);


  // console.log(user.characters)
  const userCharacterCards = (user.characters.map(character => <UserCharacterCard key={character.id} character={character} setSelectedCharacter={setSelectedCharacter}/>))


  function handleNewCharacter(e) {
    e.preventDefault();
    setIsLoading(true);
    fetch("/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ username, password }),
    }).then((r) => {
      setIsLoading(false);
      if (r.ok) {
        r.json().then((user) => onLogin(user));
      } else {
        r.json().then((err) => setErrors(err.errors));
      }
    });
  }

  return (
    <div className="account">
      <h2>Your Playthroughs</h2>
      {userCharacterCards}
      <button onClick={setShowNewCharacterForm(true)}>New Playthrough</button>
      {showNewCharacterForm ? 
      <div className="new-character-form-div">
        <form className="new-character-form">
          <label>Name:
            <input 
              type="text"
              name="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </label>
        </form>
      </div>
      : <></>}
    </div>
  )
}

export default CharacterSelect
