import React, {useEffect, useState} from "react";
import UserCharacterCard from "./UserCharacterCard"

function CharacterSelect( {user, setSelectedCharacter} ) {
  const [showNewCharacterForm, setShowNewCharacterForm] = useState(false)
  const [showStartGame, setShowStartGame] = useState(false)


  // console.log(user.characters)
  const userCharacterCards = (user.characters.map(character => <UserCharacterCard key={character.id} character={character} setSelectedCharacter={setSelectedCharacter} setShowStartGame={setShowStartGame}/>))

  function createNewCharacter() {
    setShowNewCharacterForm(true)
  }

  return (
    <div className="account">
      <h2>Your Characters</h2>
      {userCharacterCards}
      <button onClick={createNewCharacter}>Create a New Character</button>
    </div>
  )
}

export default CharacterSelect
