import React, { useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import Button from "../styles/Button";

function Login( { onLogin } ) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate()


  // const validationSchema = Yup.object().shape({
  //   username: Yup.string().matches(/^[a-zA-Z0-9]*$/, 'Invalid username').required('Required'),
  //   password: Yup.string().matches(/^[a-zA-Z0-9]*$/, 'Invalid password').required('Required'),
  // });

  const handleSubmit = async (values, { setSubmitting }) => {

    try {
      // Send login request to your backend
      const response = await fetch('/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        setTimeout(() => {
          navigate(`/`)
        }, 125)
        const user = await response.json();
        onLogin(user);
      } else {
        alert('Try again')
        onLogin(null)
        const error = await response.json();
        console.error('Login failed:', error);
      }
    } catch (error) {
      console.error('Error during login:', error);
    }

    setIsLoading(false);
  }; 

  // function handleLogin(e) {
  //   e.preventDefault();
  //   setIsLoading(true);
  //   fetch("/login", {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json",
  //     },
  //     body: JSON.stringify({ username, password }),
  //   }).then((r) => {
  //     setIsLoading(false);
  //     if (r.ok) {
  //       r.json().then((user) => {
  //         onLogin(user)
  //         navigate('/')
  //       });
  //     } else {
  //       r.json().then(() => {
  //         // const error = r.json()
  //         console.error('Login failed:', r.json());
  //         alert('No user found!')
  //         onLogin(null)
  //         // window.location.reload()
  //         // setClientLoggedIn(false)
  //       }
  //       );
  //     }
  //   });
  // }

  return (
    <>
      {/* <NavBar/> */}
      <div className="row"> {/* make this add to 12 */}
        <div className="col-3"></div>
        <div className="col-6 form-div">
          <h1>Log In</h1>
          <form onSubmit={handleSubmit}>
            <label>Username:
              <input 
                type="text"
                name="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </label>
            <br/>
            <label>Password:
              <input 
                type="text"
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </label>
            <br/>
            <Button variant="fill" color="primary" type="submit">
              {isLoading ? "Loading..." : "Login"}
            </Button>
            <br/>
            <NavLink to="/signup">Sign Up</NavLink>
          </form>
        </div>
        <div className="col-3"></div>
      </div>
    </>
  )
}


export default Login
