import React, { useState } from "react";
import { NavLink, useHistory } from "react-router-dom";

function UserCharacterCard( { character, setSelectedCharacter, setShowStartGame } ) {

  function selectCharacter(history) {
    // console.log("character selected", character)
    setSelectedCharacter(character)
    setShowStartGame(true)
    // useHistory.push("/startgame")
  }

  if (character) {
    // console.log(character)
    return (
      <div className="user-character-card-div">
        <h3>Name: {character.name}</h3>
        <p>Last Played: {character.datetime_last_played}</p>
        <button onClick={() => selectCharacter(character)}>Select Character</button>
        <p>------------------------------------------------------</p>
      </div>
    )
  }
  else {
    return (
      <></>
    )
  }

}

export default UserCharacterCard
